import 'package:schoolexam/exams/models/submission.dart';

class Remark {
  /// Base64 encoded PDF file
  final String data;

  /// We only associate remarks to submissions. This can be expanded in future versions to allow
  /// for association to correctables in general.
  final Submission submission;

  const Remark({required this.data, required this.submission});

  Remark.empty({required this.submission})
      : this.data =
            "";
}
